import React, {useState, useEffect} from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';
import {Link} from "react-router-dom";
import {Cookies} from 'react-cookie';


function Users() {
    axios.defaults.xsrfHeaderName = "X-TOKENIZERS";
    axios.defaults.xsrfCookieName = "tokenizers";

    const [users, setUsers] = useState([])

    useEffect(() => {
        axios({
            method: "GET",
            url: 'http://127.0.0.1:8000/api/users/'
        }).then(response => {
            setUsers(response.data)
        })
    }, [])


    const handleDelete = (id) => {
            // Whatever you want to do with that item
            axios.delete(`http://127.0.0.1:8000/api/users/7/`,
                {
                    withCredentials: true,
                    // headers: {
                    //     'X-CSRFTOKEN': csrfCookie,
                    //     'Content-Type': 'application/x-www-form-urlencoded',
                    //     'Authorization': 'Token ' + token
                    // }
                }).then(response => {
                setUsers(response.data)
            }).then(response => {
                console.log(response)
            }).catch(error => {
                console.log(error.response)
            })
        }
    ;
    //Delete User


    return (
        <div className="App">
            <table className="table">
                <thead>
                <tr>
                    <th scope="col">Id</th>
                    <th scope="col">User nickname</th>
                    <th scope="col">Created</th>
                    <th scope="col">Group</th>
                </tr>
                </thead>
                <tbody>
                {users.map(u => (
                    <tr>
                        <th scope="row">{u.id}</th>
                        <td>{u.username}</td>
                        <td>{u.created}</td>
                        <td>{u.group.name}</td>
                        <td><Link className="btn btn-danger" onClick={() => handleDelete(u.id)}>Delete</Link></td>
                    </tr>
                ))}

                </tbody>
            </table>

        </div>
    );
}

export default Users;
